graphql.query:
query {
    allSongs(
        name_Icontains: "Line",
        album_Icontains: "cielo",
        band_Name_Icontains: "Carrillo",
        genre_Icontains: "Orches", 
        subgenre_Icontains: "Film",
        band_SimilarBands_Icontains: "Calvin"
    ) {
        edges {
            node {
                name,
                album,
                length,
                genre,
                subgenre,
                tags,
                instruments,
                band {
                    name,
                    similarBands
                }
            }
        }
    }
}

mutation MyMutation {
    insertSong(input: { 
        clientMutationId: "1",
        datetime: "07/03/21 06:31PM",
        externalId: 343,
        name: "prueba",
        album: "prueba",
        band: "prueba",
        artist: "prueba",
        length: "02:55",
        genre: "prueba",
        subgenre: "prueba",
        tags: "prueba",
        instruments: "prueba",
}) {
        song {
            datetime
            externalId
            name
            album
            artist
            length
            genre
            subgenre
            tags
            instruments,
            band {
                name
            }
        }
