}

query {
    allBands(
        name_Icontains: "Ignacio Lopez",
        similarBands_Icontains: "Glenn"
    ) {
        edges {
            node {
                name,
                similarBands,
                song {
                    edges {
                        node {
                            name,
                        }
                    }
                }
            }
        }
    }
}

mutation MyMutation {
insertBand(input: { 
    clientMutationId: "1"
    name: "prueba",
    similarBands: "prueba",
}) {
